$main-color: #555555;
$acent-color: #ff6c00;
$secondary-color: #9da4bd;
$title-main-color: #303030;
$title-secondary-color: #ffffff;

$mobile: 480px;
$tablet: 768px;
$desktop: 1280px;

* {
  margin: 0;
  padding: 0;
}

a {
  text-decoration: none;
}

ul {
  list-style-type: none;
}

img {
  display: block;
  max-width: 100%;
  height: auto;
}

.container {
  margin-left: auto;
