    width: 688px;
    padding: 0 40px;
  }
  @media screen and (min-width: $desktop) {
    width: 1170px;
    padding: 0 15px;
  }
}

.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  padding: 0;
  overflow: hidden;
  border: 0;
  clip: rect(0 0 0 0);
}

.flex-center {
  display: flex;
  align-items: center;
  justify-content: center;
}

.trans {
  transition: 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

@mixin font($weight, $size, $height) {
  font-weight: $weight;
  font-size: $size;
  line-height: $height;
}

