    font-family: Open Sans;
    font-style: normal;
    font-weight: 600;
    font-size: 11px;
    line-height: 1.36;
    letter-spacing: 0.1em;
    text-transform: uppercase;
    margin-left: 80px;
    padding: 0;
  }
  
  .team-text::before {
    content: '';
    position: absolute;
    top: 6px;
    left: -80px;
    height: 1px;
    width: 60px;
    background-color: $title-main-color;
  }
  
  .section-title {
    color: $title-main-color;
    width: 418px;
    text-align: start;
    font-family: Raleway;
    font-style: normal;
    font-weight: bold;
    font-size: 42px;
    line-height: 1.17;
    letter-spacing: 0.05em;
    margin-top: 20px;
    margin-bottom: 44px;
    margin-left: 0px;
    margin-right: 0px;
  }
  
  .team-list {
  
    @media screen and (min-width: $mobile) {
      justify-content: center;
  
    }
    @media screen and (min-width: $tablet) {
      display: flex;
      flex-wrap: wrap;
  
    }
    @media screen and (min-width: $desktop) {
      display: flex;
      flex-wrap: nowrap;
      justify-content: space-between;
      text-align: center;
    }
  }
  
  .team-item {
    margin-bottom: 64px;
  
    &:last-child {
      margin-bottom: 0px;
    }
  
    @media screen and (min-width: $desktop) {
      padding-right: 30px;
      &:last-child {
        padding-right: 0px;
      }
    }
  
    .team-photo {
      margin-bottom: 30px;
      justify-content: center;
  
      @media screen and (min-width: $mobile) {
        width: 418px;
      }
      @media screen and (min-width: $tablet) {
        width: 450px;
      }
      @media screen and (min-width: $desktop) {
        display: flex;
        padding-bottom: 30px;
        justify-content: center;
        margin: 0;
        width: 370px;
      }
    }
  
    .team-title {
      text-align: center;
      margin-bottom: 0;
      margin-top: 0;
      color: $title-main-color;
      font-family: Open Sans;
      font-style: normal;
      font-weight: normal;
      font-size: 18px;
      line-height: 1.39;
      letter-spacing: 0.02em;
      padding-bottom: 8px;
    }
  
    .team-role {
      text-align: center;
      margin-top: 0px;
      margin-bottom: 0px;
      padding-bottom: 30px;
      color: $acent-color;
      font-family: Open Sans;
      font-style: normal;
      font-weight: normal;
      font-size: 14px;
      line-height: 1.36;
      letter-spacing: 0.02em;
    }
  
    .social-links {
      display: flex;
      justify-content: space-evenly;
      padding-left: 82px;
      padding-right: 82px;
      cursor: pointer;
    }
  
    .team-links {
      margin-right: 10px;
      background-color: $title-secondary-color;
      color: $title-main-color;
      border-radius: 50%;
  
      &:last-child {
        margin-right: 0px;
      }
      &:hover {
        fill: $acent-color;
      }
    }
  
    .icon {
      display: inline-flex;
      align-items: center;
      justify-content: center;
      background-color: $title-secondary-color;
      border-radius: 50%;
      width: 44px;
      height: 44px;
      cursor: pointer;
  
      &:hover {
        fill: $acent-color;
      }
    }
  
    .links {
      text-decoration: none;
      transition-property: all;
      transition-duration: 250ms;
      transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    }
  
    .team-icon {
      width: 20px;
      height: 20px;
      fill: $title-main-color;
  
      &:hover {
        fill: $acent-color;
      }
    }
  }

