.page-footer {
  background-color: #191c26;

  font-family: 'Open Sans', sans-serif;
  font-weight: 600;
  font-style: normal;
  font-size: 11px;
  line-height: 1.36;
  letter-spacing: 0.1em;
  color: $title-secondary-color;

  & h2 {
    font-family: 'Releway', sans-serif;
    font-weight: 700;
    font-size: 42px;
    line-height: 1.17;
    letter-spacing: 0.05em;
    text-align: center;

    @media screen and (min-width: $tablet) {
      text-align: left;
    }
  }
}

.footer-wrapper {
  position: relative;
}

.footer-bg-image {
  position: absolute;
  top: 557px;
  left: 0;
  bottom: 0;
  right: 0;

  pointer-events: none;
  background-image: url('../images/foot-bg.png');
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;

  @media screen and (max-width: 344px) {
    top: 607px;
  }

  @media screen and (min-width: $tablet) {
    top: 520px;
  }

  @media screen and (min-width: $desktop) {
    top: 0;
    left: calc(50% + 15px);
  }

  @media screen and (min-device-pixel-ratio: 2),
    screen and (min-resolution: 2dppx),
    screen and (min-resolution: 192dpi) {
    background-image: url('../images/foot-bg@2x.png');
  }

  @media screen and (min-device-pixel-ratio: 3),
    screen and (min-resolution: 3dppx),
    screen and (min-resolution: 288dpi) {
    background-image: url('../images/foot-bg@3x.png');
  }

  @media screen and (min-width: $mobile + 1px) {
    background-image: url('../images/foot-bg-tabl.png');

    @media screen and (min-device-pixel-ratio: 2),
      screen and (min-resolution: 2dppx),
      screen and (min-resolution: 192dpi) {
      background-image: url('../images/foot-bg-tabl@2x.png');
    }

    @media screen and (min-device-pixel-ratio: 3),
      screen and (min-resolution: 3dppx),
      screen and (min-resolution: 288dpi) {
      background-image: url('../images/foot-bg-tabl@3x.png');
    }
  }

  @media screen and (min-width: $tablet + 1px) {
    background-image: url('../images/foot-bg-desk.png');

    @media screen and (min-device-pixel-ratio: 2),
      screen and (min-resolution: 2dppx),
      screen and (min-resolution: 192dpi) {
      background-image: url('../images/foot-bg-desk@2x.png');
    }

    @media screen and (min-device-pixel-ratio: 3),
      screen and (min-resolution: 3dppx),
      screen and (min-resolution: 288dpi) {
      background-image: url('../images/foot-bg-desk@3x.png');
    }
  }
}

.footer-top {
  @media screen and (min-width: $desktop) {
    display: flex;
    flex-basis: 50%;
    padding: 130px 0;
  }
}
.footer-appointment,
.footer-contacts {
  padding: 85px 0;

  @media screen and (min-width: $tablet) and (max-width: $desktop - 1px) {
    padding: 100px 0;
  }

  @media screen and (min-width: $desktop) {
    width: 100%;
    padding: 0;
  }
}

.form-appointment {
  &__labels {
    margin-top: 44px;

    @media screen and (min-width: $tablet) {
      display: flex;

      @media screen and (min-width: $desktop) {
        padding-right: 25px;
      }
    }
  }

  &__textarea {
    height: 75px;
    position: relative;
    margin-bottom: 44px;

    @media screen and (min-width: $tablet) {
      margin-top: 44px;
      // padding-right: 25px;
    }
    @media screen and (min-width: $desktop) {
      padding-right: 25px;
    }
  }

  & button {
    display: block;
    margin-left: auto;
    margin-right: auto;

    @media screen and (min-width: $tablet) {
      display: inline-block;
      margin: 0;
    }
  }
}

.appointment-labels {
  display: block;
  position: relative;
  text-transform: uppercase;

  @media screen and (min-width: $tablet) {
    flex-basis: 50%;

    &:last-child {
      margin-left: 30px;
    }
  }

  &__input {
    display: block;
    width: 100%;
    margin-bottom: 44px;
    padding-bottom: 8px;
    cursor: pointer;

    font-family: 'Open Sans', sans-serif;
    font-weight: 600;
    font-style: normal;
    font-size: 11px;
    line-height: 1.36;
    letter-spacing: 0.1em;
    text-transform: uppercase;
    color: rgba(157, 164, 189, 0.4);

    background-color: transparent;

    border: none;
    border-bottom: 1px solid rgba(157, 164, 189, 0.4);

    &:focus,
    &:hover {
      outline: none;
      border-bottom: 1px solid $title-secondary-color;
      color: $title-secondary-color;
    }
    &:focus {
      cursor: text;
    }

    @media screen and (min-width: $desktop) {
      margin-bottom: 0;
    }

    @media screen and (min-width: $tablet) and (max-width: $desktop - 1px) {
      margin-bottom: 0;
    }
  }

  &__text {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    padding-bottom: 8px;
    pointer-events: none;

    color: rgba(157, 164, 189, 0.4);

    opacity: 1;
    cursor: pointer;

    .appointment-labels__input:focus ~ &,
    .appointment-labels__input:not(:placeholder-shown) ~ & {
      opacity: 0;
    }
    .appointment-labels__input:hover ~ &,
    &:hover {
      color: $title-secondary-color;
    }

    @media screen and (min-width: $desktop) {
    }
  }
}

.appointment-textarea {
  display: block;
  width: 100%;
  height: 100%;

  resize: none;
  cursor: pointer;

  border: none;
  border-bottom: 1px solid rgba(157, 164, 189, 0.4);
  background-color: transparent;
  outline: none;

  font-family: 'Open Sans', sans-serif;
  font-weight: 600;
  font-style: normal;
  font-size: 11px;
  line-height: 1.36;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  color: rgba(157, 164, 189, 0.4);

  &:hover,
  &:focus {
    color: $title-secondary-color;
    border-bottom: 1px solid $title-secondary-color;
  }
  &:focus {
    cursor: text;
  }

  &__text {
    display: block;
    position: absolute;
    top: 0;
    left: 0;

    cursor: pointer;
    pointer-events: none;

    opacity: 1;

    text-transform: uppercase;
    color: rgba(157, 164, 189, 0.4);

    .appointment-textarea:focus ~ &,
    .appointment-textarea:not(:placeholder-shown) ~ & {
      opacity: 0;
    }
    .appointment-textarea:hover ~ &,
    &:hover {
      color: $title-secondary-color;
    }
  }
}

.footer-contacts__wrapper {
  @media screen and (min-width: $desktop) {
    padding-left: 115px;
  }
}

.footer-address__wrapper {
  @media screen and (min-width: $tablet) and (max-width: $desktop - 1px) {
    display: flex;
    margin-top: 40px;
  }
}

.footer-address {
  margin-top: 40px;
  margin-bottom: 60px;

  font-style: normal;
  font-weight: normal;
  font-size: 18px;
  line-height: 1.67;
  letter-spacing: 0.02em;
  color: $secondary-color;

  @media screen and (min-width: $tablet) and (max-width: $desktop - 1px) {
    margin: 0;
    flex-basis: 50%;
  }

  &__post {
    display: inline-flex;

    & span {
      padding-top: 5px;
    }
    & p {
      @media screen and (min-width: $tablet) {
        max-width: 220px;
      }
    }
  }
  &__icon {
    font-size: 0;
    padding-right: 16px;
  }
}

.footer-address__list {
  & > li:not(:last-child) {
    margin-bottom: 20px;
    & a {
      text-decoration: none;
    }

    @media screen and (min-width: $desktop) {
      margin-bottom: 15px;
    }
  }

  & a {
    display: inline-flex;
    align-items: center;
  }
  & a,
  & a:visited {
    color: inherit;
    text-decoration: underline;
  }
}

.footer-hours {
  @media screen and (min-width: $tablet) and (max-width: $desktop - 1px) {
    margin-left: 30px;
  }
  & h3 {
    position: relative;
    margin-left: 80px;

    font-size: 11px;
    font-weight: 600;
    text-transform: uppercase;

    &::before {
      content: '';
      display: block;
      position: absolute;
      top: 50%;
      left: -80px;
      width: 60px;
      height: 1px;

      transform: translate(0, -50%);

      background-color: rgba(157, 164, 189, 0.4);
    }
  }

  & p {
    margin-top: 20px;

    font-size: 18px;
    font-weight: normal;
    line-height: 1.67;
    letter-spacing: 0.02em;
    color: $secondary-color;
  }
}

//  

.footer-copyright {
  padding: 44px 0;
  background-color: #f7f7f7;
  @media screen and (min-width: $desktop) {
    padding: 56px 0;
  }
}

.footer-bottom {
  @media screen and (min-width: $tablet) {
    display: flex;
    justify-content: space-between;
    flex-direction: row-reverse;
  }

  & p {
    letter-spacing: 0.2em;
    color: $secondary-color;
  }

  &__list {
    display: flex;
    color: $title-main-color;
    justify-content: flex-end;
    margin-bottom: 20px;

    @media screen and (min-width: $tablet) {
      margin-bottom: 0;
    }

    & > li:first-child {
      position: relative;

      &::before {
        content: '';
        display: block;
        position: absolute;
        top: 50%;
        left: -100px;
        width: 60px;
        height: 1px;

        transform: translate(0, -50%);

        background-color: $title-main-color;
      }
    }

    & > li:not(:last-child) {
      margin-right: 40px;
    }

    & > li > a,
    & > li > a:visited {
      letter-spacing: 0.2em;
      color: $title-main-color;
    }
  }
}
